import { API } from "@koh/api-client";
import useSWR from "swr";
export function useCourse(cid) {
  const {
    data: course,
    error: courseError,
    mutate: mutateCourse
  } = useSWR(cid && `/api/v1/courses/${cid}`, async () => API.course.get(cid));
  return {
    course,
    courseError,
    mutateCourse
  };
}
